//SPDX-License-Identifier: MIT

pragma solidity ^0.6.0;

contract Voting {

    event Transfer(address indexed from, address indexed to, uint256 tokens);

    string _name;
    string _symbol;

    uint256 _totalSupply;
    uint256 _amount;

    bytes32 _rootHash;

    mapping (address => bool) checkRedeemed;
    mapping (address => uint256) _balances;

    constructor(string memory name, string memory symbol, bytes32 rootHash, uint256 totalSupply, uint256 amount) public {
        _name = name;
        _symbol = symbol;
        _rootHash = rootHash;
        _totalSupply = totalSupply;
        _amount = amount;
    }

    function leafHash(address leaf) private pure returns(bytes32) {
        return keccak256(abi.encodePacked(uint8(0x00), leaf));
    }

    function nodeHash(bytes32 left, bytes32 right) private pure returns(bytes32) {
        return keccak256(abi.encodePacked(uint8(0x01), left, right));
    }

    function getName() public view returns (string memory name) {
        return _name;
    }

    function getSymbol() public view returns (string memory symbol) {
        return _symbol;
    }

    function getTotalSupply() public view returns (uint256 totalSupply) {
        return _totalSupply;
    }

function redeemToken(uint256 path, address recipient, bytes32[] memory witnesses) public {

        require(checkRedeemed[recipient] == false, "Recipient has already redeemed token");

        // Compute the merkle root
        bytes32 validator = leafHash(msg.sender);
        for (uint i = 0; i < witnesses.length; i++) {
              if ((path & 0x01) == 1) {
                validator = nodeHash(witnesses[i], validator);
            } 
           else {
                validator = nodeHash(validator, witnesses[i]);
            }
            path /= 2;
        }
        // Check the merkle proof
        require(validator == _rootHash, "validation failed");

        // Redeem!
        _balances[recipient] += _amount;
        _totalSupply -= _amount;

        emit Transfer(address(0), recipient, _amount);
    }
}
